import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { DatePipe } from '@angular/common';
import { DatabaseService } from '../Services/database.service';

@Component({
  selector: 'app-tab3',
  templateUrl: 'tab3.page.html',
  styleUrls: ['tab3.page.scss']
})
export class Tab3Page {
  para: any;
  startDate: Date;
  endDate: Date;
  attendanceListData: any = [];
  constructor(private router: Router, private datePipe: DatePipe,
    private db: DatabaseService) {
    this.para = JSON.parse(localStorage.getItem('para'));
  }

  getReport() {
    console.log(this.startDate,"===", this.endDate);
    let startDate = this.datePipe.transform(this.startDate,"yyyy-MM-dd");
    let endDate = this.datePipe.transform(this.endDate,"yyyy-MM-dd");
    let selectPara = {
      "empcode":this.para.EmpID,
      "startdate": startDate,
      "enddate": endDate
    };
    console.log(selectPara);
    this.db.attendanceList(selectPara).subscribe(res => {
      console.log(res);
      if(res.code == 200) {
        this.attendanceListData = res.data;
        console.log("attendance list: ", this.attendanceListData);
      }
    }, error => {
      console.log(error);
    })
  }
  openNotifications() {
    this.router.navigateByUrl('/notifications');
  }
}
<ion-header class="">
    <ion-toolbar class="headblue">
        <ion-item class="headblue" lines="none">
            <ion-buttons>
                <ion-menu-button class="menubtn" menu="first"></ion-menu-button>
            </ion-buttons>
            <ion-title>My Attendance</ion-title>
            <div class="profileheader">
                <ion-icon class="headicon1" slot="end"
                    name="notifications-outline" (click)="openNotifications()"></ion-icon>
            </div>
        </ion-item>
    </ion-toolbar>
</ion-header>

<ion-content>
    <h3 style="margin-left: 10px; color: #297c9d;">Select Date Between</h3>
    <div>
        <ion-grid>
            <ion-row>
                <ion-col>
                    <mat-form-field appearance="outline" class="datepicker">
                        <mat-date-range-input [rangePicker]="picker">
                            <input matStartDate placeholder="Start date"
                                [(ngModel)]="startDate">
                            <input matEndDate placeholder="End date"
                                [(ngModel)]="endDate">
                        </mat-date-range-input>
                        <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
                        <mat-date-range-picker #picker></mat-date-range-picker>
                    </mat-form-field>
                </ion-col>
                <ion-col>
                    <ion-button icon-only style="--background: #297c9d; height: 48px;" (click)="getReport()">
                        <ion-icon class="search-icon" name="search"></ion-icon>
                    </ion-button>
                </ion-col>
            </ion-row>
        </ion-grid>


    </div>
    <div class="containers table-responsive">
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Check In</th>
                    <th>Check Out</th>
                    <th>Shift</th>
                    <th>Late Remarks</th>
                    <th>Early Remarks</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let data of attendanceListData;">
                    <td>{{data.inTime | date:'d-MMM-y, h:mm a'}}</td>
                    <td>{{data.outTime | date:'d-MMM-y, h:mm a'}}</td>
                    <td>{{data.shiftName}}</td>
                    <td>{{data.late_IN_Mint}}</td>
                    <td>{{data.early_OUT_Mint}}</td>
                </tr>
            </tbody>
        </table>
    </div>

    <div *ngIf="!attendanceListData">
        <img src="assets/image/nonotification.png">
    </div>
</ion-content>
